package Arrays;

public class ArrayLesson1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[] = new int[4];
		a[0] = 3;
		a[1] = 7;
		a[2] = 10;
		a[3] = 2;
		System.out.println("---printing all the elements of array---");

		for (int i = 0; i< a.length; i++) {
						System.out.println(a[i]);
		}

	}

}
